$(document).ready(function() {
    mobile(), effect(), form(), parallax();
}), $(window).on("scroll", function() {
    form(), parallax();
}), $(window).on("resize", function() {
    mobile(), form(), parallax();
});

var mobile = function() {
    window.matchMedia("(max-width: 767px)").matches ? ($("body").addClass("mobile"), 
    $(".body-wrapper").removeClass("fixed")) : $("body").removeClass("mobile");
}, parallax = function() {
    scTop = $(window).scrollTop(), wH = $(window).height(), $(".parallax-top").each(function() {
        $(this).css("top", "0");
        var a = $(this).attr("data-parallax"), b = $(this).offset().top, c = b - wH, d = c - scTop;
        scTop, $(this).css("top", d / a);
    });
}, effect = function() {
    wow = new WOW({
        boxClass: "eff",
        animateClass: "animated",
        offset: 0,
        mobile: !0,
        live: !0
    }), wow.init();
}, form = function() {
    $(window).scrollTop() > 0 ? $(".form-wrapper").addClass("bottom-active") : ($(".form-wrapper").removeClass("bottom-active"), 
    $(".down-animate").remove()), window.matchMedia("(max-width: 1500px)").matches && $(".form-wrapper").addClass("bottom-active");
};